    private static final String TASK_ID = "taskId";

    @Test(invocationCount = 3, threadPoolSize = 2)
    public void train() throws InterruptedException {
        // TODO 现在每一次都相当于是使用同样的mock的数据，也就是前面uids的随机过程对后续训练没有影响，是否有办法将二者结合。
        String taskId = random.nextInt(100)+"";
        String[] ids = {"1B","2A","3A","4A","5C","6C","7C","8B","9B","10B"};
        int count = random.nextInt(ids.length) + 1;
        System.out.println("训练uid个数={}" + count);
        String[] uids = randomUids(ids, count);
        // map client and uid information stored in MappingResult
        Map<ClientInfo, MatchResult> sumList = new HashMap<>();
        Map<Long, String> idMap = new HashMap<>();
        for (int i = 0; i < uids.length; i++) {
            idMap.put(Long.parseLong(String.valueOf(i)), uids[i]);
        }
        String matchId = taskId+"-VERTICAL_MD5-" + random.nextInt(100);
        MatchResult matchResult = new MatchResult(matchId, 10, new MappingReport("report", 10));
        sumList.put(C1.toClientInfo(), matchResult);
        sumList.put(C2.toClientInfo(), matchResult);
        sumList.put(C3.toClientInfo(), matchResult);

        MatchStartImpl.SUM_DATA_MAP.put(matchId, matchResult);
        String content = "{\"username\":\"geyan\",\"taskId\":111,\"model\":\"FederatedGB\",\"commonParams\":[{\"field\":\"matchAlgorithm\",\"name\":\"id对齐算法\",\"value\":\"VERTICAL_MD5\",\"defaultValue\":\"VERTICAL_MD5\",\"describe\":[\"VERTICAL_MD5\",\"VERTICAL_SHA1\",\"VERTICAL_RSA\",\"MIX_MD5\",\"EMPTY\"],\"type\":\"STRING\"}],\"algorithmParams\":[{\"field\":\"crossValidation\",\"name\":\"交叉验证参数\",\"value\":1,\"defaultValue\":\"0.7\",\"describe\":[\"0.0\",\"1.0\"],\"type\":\"NUMS\"},{\"field\":\"numBoostRound\",\"name\":\"树的个数\",\"value\":1,\"defaultValue\":\"50\",\"describe\":[\"1\",\"100\"],\"type\":\"NUMS\"},{\"field\":\"firstRoundPred\",\"name\":\"初始化预测值\",\"value\":\"AVG\",\"defaultValue\":\"AVG\",\"describe\":[\"ZERO\",\"AVG\",\"RANDOM\"],\"type\":\"STRING\"},{\"field\":\"maximize\",\"name\":\"maximize\",\"value\":\"true\",\"defaultValue\":\"true\",\"describe\":[\"true\",\"false\"],\"type\":\"STRING\"},{\"field\":\"rowSample\",\"name\":\"样本抽样比例\",\"value\":1,\"defaultValue\":\"1.0\",\"describe\":[\"0.1\",\"1.0\"],\"type\":\"NUMS\"},{\"field\":\"colSample\",\"name\":\"列抽样比例\",\"value\":1,\"defaultValue\":\"1.0\",\"describe\":[\"0.1\",\"1.0\"],\"type\":\"NUMS\"},{\"field\":\"earlyStoppingRound\",\"name\":\"早停轮数\",\"value\":10,\"defaultValue\":\"10\",\"describe\":[\"1\",\"20\"],\"type\":\"NUMS\"},{\"field\":\"minChildWeight\",\"name\":\"minChildWeight\",\"value\":1,\"defaultValue\":\"1\",\"describe\":[\"1\",\"10\"],\"type\":\"NUMS\"},{\"field\":\"minSampleSplit\",\"name\":\"minSampleSplit\",\"value\":10,\"defaultValue\":\"10\",\"describe\":[\"1\",\"20\"],\"type\":\"NUMS\"},{\"field\":\"lambda\",\"name\":\"lambda\",\"value\":1,\"defaultValue\":\"1\",\"describe\":[\"1\",\"20\"],\"type\":\"NUMS\"},{\"field\":\"gamma\",\"name\":\"gamma\",\"value\":0,\"defaultValue\":\"0\",\"describe\":[\"0\",\"1\"],\"type\":\"NUMS\"},{\"field\":\"scalePosWeight\",\"name\":\"scalePosWeight\",\"value\":1,\"defaultValue\":\"1\",\"describe\":[\"0\",\"1\"],\"type\":\"NUMS\"},{\"field\":\"numBin\",\"name\":\"特征分桶个数\",\"value\":33,\"defaultValue\":\"33\",\"describe\":[\"33\",\"50\"],\"type\":\"NUMS\"},{\"field\":\"evalMetric\",\"name\":\"evalMetric\",\"value\":[\"RMSE\"],\"defaultValue\":\"MAPE\",\"describe\":[\"RMSE\",\"MAPE\",\"MSE\",\"F1\",\"ACC\",\"AUC\",\"RECALL\",\"PRECISION\",\"MACC\",\"MERROR\"],\"type\":\"MULTI\"},{\"field\":\"maxDepth\",\"name\":\"maxDepth\",\"value\":2,\"defaultValue\":\"7\",\"describe\":[\"2\",\"20\"],\"type\":\"NUMS\"},{\"field\":\"eta\",\"name\":\"learning rate\",\"value\":0.3,\"defaultValue\":\"0.3\",\"describe\":[\"0.01\",\"1\"],\"type\":\"NUMS\"},{\"field\":\"objective\",\"name\":\"objective\",\"value\":\"binaryLogistic\",\"defaultValue\":\"count:poisson\",\"describe\":[\"reg:logistic\",\"reg:square\",\"count:poisson\",\"binary:logistic\",\"multi:softmax\",\"multi:softprob\"],\"type\":\"STRING\"},{\"field\":\"numClass\",\"name\":\"(仅多分类问题)类别数量\",\"value\":1,\"defaultValue\":\"1\",\"describe\":[\"1\",\"100\"],\"type\":\"NUMS\"},{\"field\":\"bitLength\",\"name\":\"同态加密比特数\",\"value\":\"bit1024\",\"defaultValue\":\"1024\",\"describe\":[\"512\",\"1024\",\"2048\"],\"type\":\"STRING\"},{\"field\":\"catFeatures\",\"name\":\"catFeatures\",\"value\":\"1\",\"defaultValue\":\"\",\"describe\":[],\"type\":\"STRING\"},{\"field\":\"randomizedResponseProbability\",\"name\":\"randomizedResponseProbability\",\"value\":0,\"defaultValue\":\"0\",\"describe\":[\"0\",\"1\"],\"type\":\"NUMS\"},{\"field\":\"differentialPrivacyParameter\",\"name\":\"differentialPrivacyParameter\",\"value\":0,\"defaultValue\":\"0\",\"describe\":[\"0\",\"1\"],\"type\":\"NUMS\"},{\"field\":\"label\",\"name\":\"预测标签\",\"value\":\"price\",\"defaultValue\":\"MedInc\",\"describe\":[\"uid\",\"HouseAge\",\"Longitude\",\"AveOccup\",\"price\",\"uid\",\"Population\",\"MedInc\"],\"type\":\"STRING\"}]}";
        Map map = JsonUtil.parseJson(content);
        map.put(TASK_ID, taskId);
        content = JsonUtil.object2json(map);
        TrainStartServiceImpl trainStartService = new TrainStartServiceImpl();
        Map<String, Object> result = trainStartService.service(content); // 启动一个线程
        // 刷新查询模型的进度
        String modelToken = (String) ((Map)(result.get(ResponseConstant.DATA))).get("modelToken");
//        String modelToken = (String) ((HashMap)result.get(RequestConstant.DATA)).get("modelToken");
        System.out.println(modelToken);
        try {
            TrainContext trainContext = TrainCommonServiceImpl.trainContextMap.get(modelToken);
        } catch (NullPointerException e) {
            System.out.println("trainContextMap里不存在"+modelToken);
        }
        // 确定当前modelToken存在后, 循环查询模型进度直至modelToken被移除
        boolean flag = true;
        while (flag) {
            Thread.sleep(500l);
            // 训练完成后trainContextMap会移除当前modelToken -> java.lang.NullPointerException
            if (TrainCommonServiceImpl.trainContextMap.get(modelToken) != null) {
                TrainContext trainContext = TrainCommonServiceImpl.trainContextMap.get(modelToken);
                StringBuilder sbi = new StringBuilder();
                Arrays.stream(trainContext.getRunningType().getRunningType().split("")).forEach(sbi::append);
                System.out.println("训练返回结果: " + sbi.toString());
                if (sbi.toString().contains("complete")) {
                    flag = false;
                    Assert.assertEquals("complete", trainContext.getRunningType().getRunningType());
                    Assert.assertTrue(100d == trainContext.getPercent());
                } else {
                    System.out.println("训练已经完成了" + trainContext.getPercent() + "%");
                }
            } else {
                flag = false;
                System.out.println("模型训练完成");
            }
        }
    }

